define([
	'Class',
	'Utils'
],function(
	Class,
	Utils
) {
	var Global = new Class({
	});
	Global.setSize = function(width, height){
		if(Global.resizeTarget !== undefined && false) {
			if(width === undefined) width = Global.resizeTarget.width;
			if(height === undefined) height = Global.resizeTarget.height;
		} else {
			if(width === undefined) width = window.innerWidth;
			if(height === undefined) height = window.innerHeight;
		}
		//Global.width = $(document).width();
		//Global.height = $(document).height();
		Global.width = width;
		Global.height = height;
		Global.widthHalf = Global.width * .5;
		Global.heightHalf = Global.height * .5;
		Global.aspectRatio = Global.width / Global.height;
	}
	Global.autoClear = true;
	Global.resizeTarget = undefined;
	Global.now = 0;
	Global.contentServer = "http://hop.jam3.net/";
	Global.assetsPath = Global.contentServer + "assets/";
	Global.assetsAutoGeneratedPath = Global.contentServer + "assets/";
	Global.phpUploadPath = Global.contentServer + "assetsDropboxSymLink/";
	Global.assetsAutoGeneratedSubPath = Global.contentServer + "autoAssets/";
	Global.textureNotFound = Global.contentServer + "textures/textureNotFound.jpg";
	//Global.particleDBSavePath = "/particles";
	Global.particleDBSavePath = "/presets/save";
	Global.particleDBLoadPath = "/presets/load";
	Global.aspectRatio = 2;
	Global.mouseStrength = 1;
	Global.FloatArraysSupported = false;
	return Global;
});
